import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { login } from '../../store/authSlice';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [formError, setFormError] = useState('');
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const { isLoading, error, user } = useSelector((state) => state.auth);

    // Kullanici zaten Giris yapmiSsa ana sayfaya yOnlendir
    useEffect(() => {
        if (user) {
            navigate('/');
        }
    }, [user, navigate]);

    const validateForm = () => {
        setFormError('');

        if (!username.trim()) {
            setFormError('Kullanici adi boS olamaz');
            return false;
        }

        if (!password) {
            setFormError('Sifre boS olamaz');
            return false;
        }

        return true;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateForm()) {
            return;
        }

        try {
            await dispatch(login({ username, password })).unwrap();
            // BaSarili Giris sonrasi ana sayfaya yOnlendir
            navigate('/');
        } catch (err) {
            // Error is handled by the reducer
            console.error('Giris hatasi:', err);
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card shadow">
                        <div className="card-header bg-primary text-white">
                            <h4 className="mb-0">Giris Yap</h4>
                        </div>
                        <div className="card-body">
                            {(error || formError) && (
                                <div className="alert alert-danger">
                                    {formError || error}
                                </div>
                            )}
                            <form onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    <label htmlFor="username" className="form-label">
                                        Kullanici Adi
                                    </label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="username"
                                        value={username}
                                        onChange={(e) => setUsername(e.target.value)}
                                        placeholder="Kullanici adinizi girin"
                                        required
                                        autoFocus
                                    />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="password" className="form-label">
                                        Sifre
                                    </label>
                                    <input
                                        type="password"
                                        className="form-control"
                                        id="password"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        placeholder="Sifrenizi girin"
                                        required
                                    />
                                </div>
                                <div className="d-grid">
                                    <button
                                        type="submit"
                                        className="btn btn-primary"
                                        disabled={isLoading}
                                    >
                                        {isLoading ? (
                                            <>
                                                <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                                                Giris Yapiliyor...
                                            </>
                                        ) : (
                                            'Giris Yap'
                                        )}
                                    </button>
                                </div>
                            </form>
                            <div className="mt-3 text-center">
                                <p>Hesabiniz yok mu? <Link to="/register" className="text-decoration-none">Kaydolun</Link></p>
                                <p className="text-muted small">
                                    Test Hesabi: admin / password123
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Login;
